mapper.insert:
		

			SELECT IFNULL(MAX(FILE_NO), 0) + 1
			FROM BOARD.TB_BOARD_FILE
			WHERE
			BOARD_SEQ = #{board_seq}

		

		INSERT
		INTO BOARD.TB_BOARD_FILE
		(
		BOARD_SEQ
		, FILE_NO
		, FILE_NAME_KEY
		, FILE_NAME
		, FILE_PATH
		, FILE_SIZE
		, DEL_YN
		, INS_USER_ID
		, INS_DATE
		, UPD_USER_ID
		, UPD_DATE
		)
		VALUES
		(
	      #{board_seq}
		, #{file_no}
		, #{file_name_key}
		, #{file_name}
		, #{file_path}
		, #{file_size}
		, 'N'
		, #{ins_user_id}
		, NOW()
		, #{ins_user_id}
		, NOW()
		);

mapper.delete:
		UPDATE BOARD.TB_BOARD
		SET BOARD_SUBJECT = #{board_subject}
		, BOARD_CONTENT = #{board_content}
		, UPD_USER_ID = #{ins_user_id}
		, UPD_DATE = NOW()
		WHERE BOARD_SEQ = #{board_seq}

		UPDATE TB_BOARD
		SET BOARD_RE_SEQ = BOARD_RE_SEQ + 1
		, UPD_USER_ID = 'NONMEMBER'
		, UPD_DATE = NOW()
		WHERE BOARD_RE_REF = #{board_re_ref}
		AND BOARD_RE_SEQ > #{board_re_seq}

		UPDATE BOARD.TB_BOARD_FILE
		SET DEL_YN = 'Y'
		, UPD_USER_ID = 'NONMEMBER'
		, UPD_DATE = NOW()
		WHERE BOARD_SEQ = #{board_seq}
		AND FILE_NO = #{file_no}

mapper.select:
		

		SELECT *
		FROM BOARD.TB_REPLY
		WHERE DEL_YN = 'N' AND BOARD_SEQ = #{board_seq}
		ORDER BY REPLY_NO DESC
		LIMIT #{limit} OFFSET #{offset}

		SELECT COUNT(*)
		FROM BOARD.TB_REPLY
		WHERE DEL_YN = 'N' AND BOARD_SEQ = #{board_seq}

mapper.delete:
		

		UPDATE BOARD.TB_REPLY
		SET DEL_YN = 'Y'
		WHERE BOARD_SEQ = #{board_seq} AND REPLY_NO = #{reply_no};

mapper.insert:
			INSERT
